const Product = ({post}) => {
  return (
    <div>

      <div>
        <p>{post.title}</p>       
      </div>

      <div>
        <p>{post.description}</p>       
      </div>

      <div>
        <img src={post.image}></img>
      </div>

      <div>
        <p>{post.price}</p>
      </div>

      <button>
        {
          true ? <p>Remove Item</p> : <p>Add To Cart</p>
        }
      </button>

    </div>
  )
};

export default Product;
